/*告訴使用者現在時間是工作天還是放假天 */
/* 練習創建 加入條件式，讓系統判斷要渲染哪個元件*/

function CurrentMessage(props) {
    if (props.day > 0 && props.day < 6) {
        return (<WorkDays />)
    }
    return (<Weekends />)
}

function WorkDays() {
    return (
        <p className="WorkDays">
            努力賺$$$的時間
        </p>
    )
}

function Weekends() {
    return (
        <p className="Weekends">
            休息娛樂時間到!
        </p>
    )
}

export default CurrentMessage;
