/*告訴使用者現在時間是工作天還是放假天 */
/* 練習創建 加入條件式，讓系統判斷要渲染哪個元件*/
import {useDayContext} from './DayProvider';

const {day} = useDayContext();

function CurrentMessage({day}) {
    const workdays = (day >= 1 && day <= 5);
    const weekends = (day === 0 || day === 6 );

    if (workdays) {
        return (<WorkDays />)
    } else if (weekends) {
        return (<Weekends />)
    } else {
        return (<ErrorMessage />)
    }
}

function WorkDays() {
    switch (day) {
        case 1:
            return (<p>痛苦星期一 QQ</p>);
        case 2:
            return (<p>煎熬星期二 @@</p>);
        case 3:
            return (<p>無語星期三 O口O</p>);
        case 4:
            return (<p>希望星期四 +.+</p>);
        case 5:
            return (<p>快樂星期一 ^口^</p>);
        default:
            return (<p>資料有誤!</p>);
    }
}

function Weekends() {
    return (
        <p className="Weekends">
            休息娛樂時間到!
        </p>
    )
}

function ErrorMessage() {
    return (
        <p className="ErrorMessage">
            資料有誤!
        </p>
    )
}

export default CurrentMessage;
